package com.example.dto;import com.fasterxml.jackson.annotation.JsonInclude;import jakarta.validation.constraints.NotBlank;import jakarta.validation.constraints.NotNull;import jakarta.validation.constraints.Size;import lombok.Getter;import lombok.Setter;@Getter@Setter@JsonInclude(JsonInclude.Include.NON_NULL)public class AuthDTO {    @NotNull(message = "email is null!")    @NotBlank(message = "email is blank!")    private String email;    @NotNull(message = "email is null!")    @NotBlank(message = "email is blank!")    @Size(min = 8, message = "password should be minimum 8!")    private String password;}